export default function SearchBar({ onSearch }) {
  // ac치 va tu c칩digo

  const onButtonClick = (e) => {
    let textInput = useRef(null);
    onSearch(textInput.value);
  };
  return (
    <div>
      <input ref={textInput} />
      <button onClick={onButtonClick}>Add</button>
    </div>
  );
}
